import React, { Component } from 'react'
import Button from '../../components/UI/Button/Button'
import './ContactData.css'
import axios from '../../axios-order'
import Spinner from '../../components/UI/Spinner/Spinner'
import Input from '../../components/UI/Input/Input'

export default class ContactData extends Component {

    state={
        orderForm:{
                name:{
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'Your Name'
                    },
                    value:'',
                    validation:{
                        required:true
                    },
                    valid:false
                },
                email:{ 
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'Your Email'
                    },
                    value:'',
                    validation:{
                        required:true
                    },
                    valid:false
                },
                street:{
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'Your Street'
                    },
                    value:'',
                    validation:{
                        required:true
                    },
                    valid:false
                },
                zipCode:{
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'Your ZipCode'
                    },
                    value:'',
                    validation:{
                        required:true,
                        minLength:5,
                        maxLength:5
                    },
                    valid:false
                },
                country:{
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'Your Country'
                    },
                    value:'',
                    validation:{
                        required:true
                    },
                    valid:false
                },
                deliveryMethod:{
                    elementType:'select',
                    elementConfig:{
                        options:[
                            {value:'fastest',displayValue:'Fastest'},
                            {value:'cheapest',displayValue:'Cheapest'}
                        ]
                    },
                    value:''
                }
        },
        loading:false
    }

    orderHandler=(e)=>{
        e.preventDefault();
        this.setState({loading:true});
        const formData={};
        for(let formElementIdentifier in this.state.orderForm){
            formData[formElementIdentifier]=this.state.orderForm[formElementIdentifier].value;
        }
        const order={
            ingredients:this.props.ingredients,
            price:this.props.price,
            orderData:formData
        }
        axios.post('/orders.json',order)
        .then(response=>{
            
            this.setState({loading:false});
            this.props.history.push('/');
        })
            .catch(error=>this.setState({loading:false})
            );
    }

    checkValidity(value,rules){
        let isValid=false;
        if(rule.required){
            isValid=value.trim() !== '';
        }
        if(rules.minLength){
            isValid=value.length>=rules.minLength;
        }
        return isValid;
    }
    inputChangedHandler=(event,inputIdentifier) =>{
        const updatedOrderForm={
            ...this.state.orderForm
        }
        const updatedFormElement={...updatedOrderForm[inputIdentifier]};
        updatedFormElement.value=event.target.value;
        updatedFormElement.valid=this.checkValidity(updatedFormElement.value,updatedFormElement.validation);
        updatedOrderForm[inputIdentifier]=updatedFormElement;
        this.setState({orderForm:updatedOrderForm})
    }
    render() {

        const formElementsArray=[];
        for(let key in this.state.orderForm){
            formElementsArray.push({
                id:key,
                config:this.state.orderForm[key]
            });
        }
        let form=(
            <form onSubmit={this.orderHandler}>
                {/* <Input inputtype="input" type="text" name="name" placeholder="Enter Name" />
                <Input inputtype="input" type="email" name="email" placeholder="Enter email"/>
                <Input inputtype="input" type="text" name="street" placeholder="Enter street"/>
                <Input inputtype="input" type="text" name="postalCode" placeholder="Enter postal code"/> */}

                
                {formElementsArray.map(formElement=>(
                    <Input 
                        key={formElement.id}
                        elementType={formElement.config.elementType}
                        elementConfig={formElement.config.elementConfig}
                        value={formElement.config.value}
                        changed={(event)=>this.inputChangedHandler(event,formElement.id)}/>
                
                ))}
                <Button btnType="Success" >ORDER</Button>
            </form>
        );
        if(this.state.loading)
        {
            form=<Spinner/>
        }
        return (
            <div className="ContactData">
                <h4>Enter your contact data</h4>
                {form}
            </div>
        )
    }
}
